@import url("https://fonts.googleapis.com/css?family=Oxygen");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

html, body {
  height: 100%; }

html {
  font-size: 62.5%;
  overflow-y: scroll; }

body {
  font-size: 1.3rem;
  background: #1e272e;
  color: white;
  font-family: 'Oxygen', Helvetica, sans-serif; }

.link--active {
  border-bottom: 2px solid #ff5252; }

.container {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  width: 90vw;
  margin: 0 auto; }

.container__component {
  flex: 1;
  display: flex;
  justify-content: center; }

.header {
  align-items: center;
  display: flex;
  justify-content: space-between;
  padding: 2rem 1rem; }
  .header a {
    color: lightgray;
    padding-bottom: 0.2rem;
    text-decoration: none;
    transition: border 0.2s ease; }
    .header a:hover {
      border-bottom: 2px solid #ff5252; }

.header__home a {
  color: #ff5252; }

.footer {
  align-items: center;
  display: flex;
  justify-content: center;
  padding: 2rem 1rem; }
  .footer a {
    color: white;
    font-size: 2rem;
    margin-left: 2rem; }

.home {
  display: flex;
  align-items: center; }

.home__info {
  display: flex;
  flex-direction: column;
  align-items: center; }
  .home__info p {
    font-size: 6rem; }
    .home__info p span {
      color: #ff5252; }
    .home__info p:nth-child(3) {
      font-size: 3rem;
      margin-bottom: 3rem;
      margin-top: 1rem; }

.home__splitter {
  height: 75vh;
  border-right: 2px solid #ff5252;
  margin: 5rem; }

.home__picture img {
  height: 50vh;
  border-radius: 50%; }

.button {
  background: #ff5252;
  border: none;
  border-bottom: 3px solid #b80000;
  border-radius: 1rem;
  color: white;
  cursor: pointer;
  font-size: 2rem;
  padding: 2rem 4rem 2rem 4rem;
  transition: box-shadow 1500ms linear; }
  .button:active {
    border-bottom: none;
    margin-top: 3px; }
  .button:hover {
    -moz-box-shadow: 0 0 50px #ff6c6c;
    -webkit-box-shadow: 0 0 50px #ff6c6c;
    box-shadow: 0px 0px 50px #ff6c6c; }
  .button:focus {
    background-image: none;
    outline: 0;
    -webkit-box-shadow: none;
    box-shadow: none; }
